mapper.update:
update inquiry
set i_del = 'Y'
where i_id = #{iId}

update inquiry
set i_title = #{iTitle}, i_contents = #{iContents}
where i_id = #{iId}

update inquiry
set i_answer = #{iAnswer}, i_a_date = sysdate, i_state = 'Y'
where i_id = #{iId}

update inquiry
set i_answer = #{iAnswer}
where i_id = #{iId}

update inquiry
set i_answer = null, i_a_date = null, i_state = 'N'
where i_id = #{iId}
