mapper.update:
update member
set email=#{email}, gender=#{gender}, birth_date=#{birthDate}, phone=#{phone}, address=#{address}
where m_id=#{id}

update member
set drop_yn='Y'
where m_id=#{id}

mapper.select:
select count(*)
from member
where m_id=#{id}

select m_id 
from member 
where email = #{email}

mapper.update:
update member
set m_pwd = #{pwd}
where m_id = #{id} and email = #{email}

update member
set m_pwd = #{pwd}
where m_id = #{id}
