mapper.update:
update review
set r_title = #{rTitle}, r_contents = #{rContents}, r_image = #{rImage}
where r_id = #{rId]

update review
set r_del = 'Y'
where r_id = #{rId}

mapper.select:
select count(*)
from review_commend
where m_no = #{mNo} and r_id = #{rId}

mapper.update:
update review
set r_commend = r_commend - 1
where r_id = #{rId}

update review
set r_commend = r_commend + 1
where r_id = #{rId}

update REVIEW_REPLY
set r_r_del = 'Y'
where r_r_id = #{rrId}
